void DetailsScreenViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &homeScreenButton)
    {
        //ScreenChange
        //When homeScreenButton clicked change screen to HomeScreen
        //Go to HomeScreen with screen transition towards North
        application().gotoHomeScreenScreenSlideTransitionNorth();
    }
    if (&src == &ModalWindowButton)
    {
        //ShowModalWindow
        //When ModalWindowButton clicked show modalWindow1
        //Show modalWindow1
        modalWindow1.setVisible(true);
        modalWindow1.invalidate();
    
        //ShowModalButton
        //When ShowModalWindow completed show closeModalButton
        //Show closeModalButton
        closeModalButton.setVisible(true);
        closeModalButton.invalidate();
    }
    if (&src == &closeModalButton)
    {
        //CloseModalWindow
        //When closeModalButton clicked hide modalWindow1
        //Hide modalWindow1
        modalWindow1.setVisible(false);
        modalWindow1.invalidate();
    }
}
